    }



    render() {

        return (
            <div className="App">
                <header className="App-header">
                    <h1>Ranky - Book a Venue</h1>
                </header>
                <div className="Cards">
                    {bars.map(( bar ) => (
                        <Card className="card">
                            <CardActionArea>
                                <CardMedia
                                    className="media"
                                    image={dj}
                                    title={bar.name}
                                />
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="h2">
                                        {bar.name}
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary" component="p">
                                        {bar.description}
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                            <CardActions>
                                <Button size="small" color="primary">
                                    Book
                                </Button>
                                <Button size="small" color="primary">
                                    Learn More
                                </Button>
                            </CardActions>
                        </Card>
                    ))}
                </div>
            </div>
        );
    }
}

export default App;
